func:
if(msg.topic == 'newSR'){
    msg.j = 0;
    // msg.loops = Math.max([1, msg.minStrain/msg.nowSr])/msg.delay;
    // msg.loops = msg.minStrain/msg.nowSr/msg.delay;
    msg.loops = 2; 
    msg.topic='Running';
}

if (msg.topic == 'Running'){
    if(msg.j < msg.loops){
        msg.j += 1;
        // read sensors once
        msg.payload = msg.nowSr;

        return [msg, msg];
    }
}
msg.topic='End';
return [msg, null];

if( context.i == undefined ){
    context.i = 0;
}else{
    context.i += 1;
}

if( context.items == undefined ){
    context.items = msg.payload;
}
if(context.i < context.items.length){
    msg.nowSr = context.items[context.i];
    return msg;
}else{
    context.i = undefined;
    context.items = undefined;
}

switch (msg.topic){
    case "Init":
        var msg1 =  { "payload": 19};
        var msg2 =  { "payload": 18};
        var msg3 = { "payload": 34};
        var msg4 = { "payload": 35};
        var msg5 =  { "payload": 3200 };
        var msg6 =  { "payload": 7.29 };
        var msg7 =  { "payload": 1 };
        var msg8 =  { "payload": 9 };
        var msg9 =  { "payload": 0.9 };
        var msg10 =  { "payload": 1000 };
        var msg11 =  { "payload": 25.0};
        var msg12 = { "payload": [1, 2, 3]};
        var msg13 = { "payload": "/UserData/"};
        var msg14 = { "payload": "Untitled" };
        node.send([msg1, msg2, msg3, msg4, msg5, msg6, msg7, msg8, msg9, msg10, msg11, msg12, msg13, msg14])
    default:
        // return msg;
}
return;

if(msg.topic == "runtime/pDiff"){

    var startmoment = context.get("startmoment")
    var inData = context.get("inData") || []
    var outData = context.get("outData") || []

    var nowData = msg.payload.split(' ').map(Number);

    if(startmoment === undefined){
        startmoment = new Date().getTime();
        context.set("startmoment",startmoment)
    }

    var time = new Date().getTime() - startmoment
    // create chart point object

    var inOb = {x:time, y:nowData[0]}
    inData.push(inOb)

    var outOb = { x:time, y:nowData[1]}
    outData.push(outOb)

    // limit data collection length
    if(inData.length > 1000){
        inData.shift()
        outData.shift()

    }

    context.set("inData",inData);
    context.set("outData", outData);


    //prepare object for chart
    //see how and why https://github.com/node-red/node-red-dashboard/blob/master/Charts.md
    msg.payload = [{
    "series": ["Inlet", "Outlet"],
    "data": [inData, outData],
    "labels": ["Inlet", "Outlet"]
    }]


    return msg;
}


if(msg.topic == "clear"){
    startmoment = undefined;
    context.set("startmoment",startmoment)
    context.set("inData",[]);
    context.set("outData", []);
    msg.payload = []
    return msg
}

return null;

// read global parameters 
    var initParams = flow.get('initParams');
    var expCount = flow.get('expCount');
    expCount = expCount + 1;
    flow.set("expCount", expCount);
    // var dP = initParams.dirPin;
    // var sP = initParams.stepPin;
    // var stepPerRev = initParams.stepPerRev;
    // var ID = initParams.iDia;
    // var pch = initParams.pch;
    // var L = initParams.Lth;
    // var H = initParams.Hgt;
    // var splPrd = initParams.splPrd;
    // var minStrain = initParams.minStrain;
    // var sr = initParams.sr;

    msg.delay = initParams.splPrd;
    msg.minStrain = initParams.minStrain;
    try {
        msg.payload = initParams.sr.split(',').map(Number);
    } catch (e) {
        msg.payload = initParams.sr;
    }
    // msg.topic = 'Start';
    context.i = undefined;
    context.items = undefined;
    context.topic = undefined;
    var msg1 = {};
    msg1.payload = initParams.dirPin + ' ' + initParams.stepPin;
    var msg2 = {};
    msg2.payload = initParams.pInPin + ' ' + initParams.pOutPin;
    var msg3 = {};
    msg3.payload = initParams.stepPerRev;
    var msg4 = {};
    msg4.payload = initParams.iDia + ' ' + initParams.pch;
    var msg5 = {};
    msg5.payload = initParams.Lth + ' ' + initParams.Hgt;
    var msg6 = {};
    msg6.payload = initParams.splPrd;
    var msg7 = {};
    msg7.payload = initParams.minStrain;
    return [msg, msg1, msg2, msg3, msg4, msg5, msg6, msg7];

if (msg.topic === "save") {
    msg.payload = context.toSave;
    var expCount = flow.get("expCount");
    msg.filename = context.savepath + '/' + context.savename + '_' + expCount + '.data';
    return msg;
}
else if (msg.topic === "savename"){
    context.savename = msg.payload;
}
else if (msg.topic === "savepath"){
    context.savepath = msg.payload;
}else{
    context.toSave = msg.payload;
}
return null;

// save parameters to context
var initParams = flow.get("initParams")
if(initParams === undefined){
    msg.topic="Init";
}
flow.set("initParams", msg.payload);
return msg;

if (msg.topic == 'Start'){
    context.i = 0;
    context.items = msg.payload;
    context.topic = 'Start';
}
if ((context.i != undefined) && (context.topic == 'Start')){
    if(context.i < context.items.length){
        msg.nowSr = context.items[context.i] + ' 1';
        context.i += 1;
        return msg;
    }else{
        context.topic = undefined;
    }
}
return null;

if (msg.topic == 'Rinse'){
    context.dir = 0;
    var initParams = flow.get("initParams")
    context.sr = initParams.sr;
    context.topic = 'Rinse';
}
if (msg.topic == 'RinseStop'){
    context.topic = undefined;
}
if (context.topic == 'Rinse') {
    msg.payload = context.sr + ' ' + context.dir;
    context.dir = 1 - context.dir;
    return msg;
}

// save parameters to context
var expCount = flow.get("expCount");
if (expCount === undefined){
    flow.set("expCount", 0);
}
return msg;
